frappe.ui.form.on('Stock Entry', {
	refresh(frm) {
        frm.fields_dict.items.grid.toggle_enable("qty",!frm.doc.stock_check);
        frm.fields_dict.items.grid.toggle_display("checking_qty",frm.doc.stock_check);
        frm.fields_dict.items.grid.set_column_disp("checking_qty",frm.doc.stock_check);
        if(frm.doc.docstatus===1 && frm.doc.stock_entry_type == "Manufacture") {
			frm.add_custom_button(__("Barcode Printing"), function() {
            frappe.call({
                method:'barcode_shrdc.barcode_shrdc.doctype.barcode_printing.barcode_printing.create_barcode_printing',
                args:{ throw_if_missing: true, 
                    se_id: frm.doc.name,
                    pr_id: null}

            })
				.then(r => {
					if (r.message) {
					    console.log(r.message)
						let new_bp_doc = r.message;	// new barcode printing request
						frappe.set_route("Form", new_bp_doc.doctype, new_bp_doc.name);
					}
				})
			},  __("Create"));
			frm.page.set_inner_btn_group_as_primary(__('Create'));

		}
	},
	onload(frm) {
        frm.fields_dict.items.grid.toggle_enable("qty",!frm.doc.stock_check);
        frm.fields_dict.items.grid.toggle_display("checking_qty",frm.doc.stock_check);
        frm.fields_dict.items.grid.set_column_disp("checking_qty",frm.doc.stock_check);
	},
	scan_barcode: function(frm){
	    let scan_barcode_field = frm.fields_dict["scan_barcode"];

		let show_description = function(idx, exist = null) {
			if (exist) {
				scan_barcode_field.set_new_description(__('Row #{0}: Qty increased by 1', [idx]));
			} else {
				scan_barcode_field.set_new_description(__('Row #{0}: Item added', [idx]));
			}
		};

		if(frm.doc.scan_barcode) {
			frappe.call({
				method: "erpnext.selling.page.point_of_sale.point_of_sale.search_serial_or_batch_or_barcode_number",
				args: { search_value: frm.doc.scan_barcode }
			}).then(r => {
				const data = r && r.message;
				if (!data || Object.keys(data).length === 0) {
					scan_barcode_field.set_new_description(__('Cannot find Item with this barcode'));
					return;
				}

				let cur_grid = frm.fields_dict.items.grid;

				let row_to_modify = null;
				const existing_item_row = frm.doc.items.find(d => d.item_code === data.item_code);
				const blank_item_row = frm.doc.items.find(d => !d.item_code);

				if (existing_item_row) {
					row_to_modify = existing_item_row;
				} else if (blank_item_row) {
					row_to_modify = blank_item_row;
				}

				if (!row_to_modify) {
					// add new row
					row_to_modify = frappe.model.add_child(frm.doc, cur_grid.doctype, 'items');
					frappe.msgprint({
                    title: __('Alert'),
                    indicator: 'orange',
                    message: __('New Item is added')
                    });
				}

				show_description(row_to_modify.idx, row_to_modify.item_code);

				frm.from_barcode = true;
				frappe.model.set_value(row_to_modify.doctype, row_to_modify.name, {
					item_code: data.item_code,
					checking_qty: (frm.doc.stock_check)?(row_to_modify.checking_qty || 0) + 1 : row_to_modify.checking_qty,
					qty: (!frm.doc.stock_check)?(row_to_modify.qty || 0) + 1 : row_to_modify.qty
				});

				['serial_no', 'batch_no', 'barcode'].forEach(field => {
					if (data[field] && frappe.meta.has_field(row_to_modify.doctype, field)) {

						let value = (row_to_modify[field] && field === "serial_no")
							? row_to_modify[field] + '\n' + data[field] : data[field];

						frappe.model.set_value(row_to_modify.doctype,
							row_to_modify.name, field, value);
					}
				});

				scan_barcode_field.set_value('');
				refresh_field("items");
			});
		}
	},
	stock_check: function(frm)
	{
        frm.fields_dict.items.grid.toggle_enable("qty",!frm.doc.stock_check);
        frm.fields_dict.items.grid.toggle_display("checking_qty",frm.doc.stock_check);
        frm.fields_dict.items.grid.set_column_disp("checking_qty",frm.doc.stock_check);
        if (frm.doc.stock_entry_type == "Receive at Warehouse")
        {
            if(frm.doc.stock_check)
            {
                frm.doc.items.forEach(function(d){
                    d.serial_no = ""
                })
            }
            else
            {
                frappe.call({
        			doc: frm.doc,
        			method: "set_items_for_stock_in",
        			callback: function() {
        				refresh_field('items');
        			}
        		}); 
            }

        }
	},
	before_submit: function(frm,cdt,cdn){
		const item = locals[cdt][cdn];
		if(frm.doc.stock_check)
		{
		    frm.doc.items.forEach(function(d)
            {
    		    if(d.qty !== d.checking_qty) 
    		    {
    		        frappe.validated = false;
                    frappe.msgprint({
                    title: __('Warning'),
                    indicator: 'red',
                    message: __('Checking Qty and Required Qty of Items row {0} does not matched.',[d.idx])
                    });
    		    }
    		    if (frm.doc.stock_entry_type=="Receive at Warehouse")
    		    {
    		        frappe.call({
                			args: {name:frm.doc.outgoing_stock_entry},
                			method: "get_stock_entry",
                			callback: function(r) {
                				console.log(r.message);
                				var send_doc = r.message;
            				    var send_item = send_doc.items.find(send => send.item_code === d.item_code);
                		        var send_serials = send_item.serial_no.split("\n");
                		        console.log(send_serials);
                		        var serials = d.serial_no.split("\n");
                		        if (serials[serials.length-1]=='')
                		        {
                		            serials.pop();
                		        }
                		        const containsAll = serials.every(element => {
                                  return send_serials.includes(element);
                                });
                                console.log(containsAll);

                		        if (!containsAll)
                		        {
                                    frappe.validated = false;
                                    frappe.msgprint({
                                    title: __('Warning'),
                                    indicator: 'red',
                                    message: __('Serial No. of Items row {0} does not match with Stock Entry (Outward GIT) {1}.',[d.idx, frm.doc.outgoing_stock_entry])
                                });
                		        }
                			}
                		}); 


    		      //  console.log(send_doc);


    		    }
            });
		}
	},
	before_save(frm)
	{
		if(!frm.doc.stock_check)
		{
    		frm.doc.items.forEach(function(d)
                {
                    frappe.model.set_value(d.doctype,d.name,'checking_qty',0);
                }
            );
		}
	}
