css:
.label-item-name { 
    grid-area: label-item-price;
    font-size: 1.1em;
    font-weight: 600;
}
.label-item-price { 
    grid-area: label-item-price;
    font-size: 1.1em;
    font-weight: 400;
}

.label-field2{
    margin: auto;
    text-align: center;
}

.label-item-code { 
    grid-area: label-item-code;
    width: 100%;
    height: 1.2em;
    font-size: 1.3em;
    overflow-wrap: break-word;
}

.label-item-code-alone { 
    grid-area: label-item-code;
    width: 100%;
    height: 3em;
    font-size: 3em;
    overflow-wrap: break-word;
    margin: auto;
}

.label-item-barcode { 
    grid-area: label-item-barcode;
    width: 100% !important;
    font-size: 0.5em;
    text-align: center;
}
.barcode-container{
    max-width: 100%;
    max-height: 100%;
}

.print-format{
font-family: monosapce;
}

 .barcode-label-container{
    border: 1px solid #d1d8dd;
    display: inline-block;
    resize: both;
    overflow: auto;
    padding:0.5em;
    width: 100%;
    height: 100% 
    
}
 
.dashboard-section{
		display: inline-block;
		resize: both;
		overflow: auto;
}
.label-field{
    margin: auto;
    //border: 1px solid black;
    text-align: center;
    font-family: monosapce;
}

.row:after {
  content: "";
  display: table;
  clear: both;
}

