{% set item_code_font_size = config.item_code_font_size %}
{% set item_name_font_size = config.item_name_font_size %}
{% set item_price_font_size = config.item_price_font_size %}
{% set qrcodes = doc.qrcodes.split('<br>') %}
{% set temp = qrcodes.pop() %}
{% set qrcode_no = namespace(value=0) %}
{% for item in items  %}
    {% set barcode_format = item.barcode_type %}
    {% set barcode = item.barcode %}
    {% set item_code = item.item_code %}
    {% set item_name = item.item_name %}
    {% set item_price = item.rate %}
    {% set item_batch = item.batch_no %}
    {% set item_serials = item.serial_no %}
    {% set qty = item.qty %}
    {% if item_serials %} 
    {% set serials = item_serials.split('\n') %}
    {% if serials |last == "" %}
    {% set empty = serials.index("") %}
    {# pop the number two from myList #} 
    {% set temp = serials.pop() %}
    {% endif %}
    {% for serial in serials %}
          <div class="barcode-label-container">
          <div class="label-item-code label-field" style = "font-size:{{item_code_font_size}}em">
           {{ item_code  }}
          </div>
              <div class="label-item-barcode label-field">
                <div class="barcode-container">
                    {{qrcodes[qrcode_no.value]}}
                </div>
              </div>
              <div class = "row2">
                    <div class="label-item-name label-field2" style = "font-size:{{item_name_font_size}}em">
                          {{ item_name }}
                    </div>
                    <div class="label-item-price label-field2" style = "font-size:{{item_price_font_size}}em">
                        RM {{"%.2f"|format(item_price)}}
                    </div>
            </div>
        </div>
        {% set qrcode_no.value = qrcode_no.value +1 %}
    {% endfor %}
    {% else %}
        {% for i in range((qty) |round |int ) %}
          <div class="barcode-label-container">
          <div class="label-item-code label-field" style = "font-size:{{item_code_font_size}}em">
           {{ item_code  }}
          </div>
              <div class="label-item-barcode label-field">
                <div class="barcode-container">
                    {{qrcodes[qrcode_no.value]}}
                </div>
              </div>
              <div class = "row2">
                    <div class="label-item-name label-field2" style = "font-size:{{item_name_font_size}}em">
                          {{ item_name }}
                    </div>
                    <div class="label-item-price label-field2" style = "font-size:{{item_price_font_size}}em">
                        RM {{"%.2f"|format(item_price)}}
                    </div>
            </div>
        </div>
        {% endfor %}
        {% set qrcode_no.value = qrcode_no.value +1 %}
    {% endif%}
{% endfor %}

