          </div>
        </div>
      </div>
      {% endfor %}
  {% endif%}
  {%endif %}
  {# ITEM SERIAL NO #}
     {% if doc.show_serial_no  %}
     {% if item_serials %}
    {% set serial_format = doc.serial_barcode_type%}
    {% set serials = item_serials.split('\n') %}
        {% if serials |last == "" %}
    {% set empty = serials.index("") %}
    {# pop the number two from myList #} 
    {% set temp = serials.pop() %}
